(function() {

    $(window).on('load', function() {

        //preloader code start
        var preloader = $('.loader-overlay');
        if(preloader.length) {
            preloader.fadeOut(1000, function () {
                //$(this).remove();
            });
        }
        //preloader code end

    });

    $(document).ready(function() {
		$(".sidebar-switcher").click(function(a) {
            a.preventDefault(),
            $(this).parent().hasClass("open") ? $(this).parent().removeClass("open") : $(this).parent().addClass("open")
        });
        var d = $("link[rel=stylesheet][title]");
        if (d.length) {
            var e = d.attr("title");
            $(".sidebar").find(".bg-" + e).addClass("active"),
            $(document).on("click.stylesheet", ".color-switcher",
            function(a) {
                a.preventDefault(),
                $(".color-switcher").removeClass("active"),
                $(this).addClass("active");
                var b = $(this).data("color");
                $("link[rel~=stylesheet][title]").each(function() {
                    var a = $(this);
                    a.prop("disabled", a.attr("title") !== b)
                })
            })
        }
        //scrolling to the top code start
        $("#to-top").click(function(){
            $("html, body").animate({
                scrollTop:0
            },500);
            return false;
        });
        function appearScroller() {
            if($(window).scrollTop() > 50){
                $("#to-top").addClass("active");
            } else {
                $("#to-top").removeClass("active");
            }
        }
        appearScroller();
        $(window).scroll(function(){
            appearScroller();
        });
        //scrolling to the top code end

        var url = 'li[id="' + window.location.pathname.split("/")[1].split(".")[0] + '"]';
        $("#navbar-main").find(url).addClass("active");
         //console.log(url);
         //console.log("1239999999999999999999999999999");
         //console.log($("#navbar-main").find(url).addClass("active"));
     

    });

})();